import React, { useEffect, useState } from 'react';

const WordDetails = ({ word }) => {
const [wordDetails, setWordDetails] = useState(null);

useEffect(() => {
const fetchWordDetails = async () => {
      try {
        // Replace 'YOUR_API_KEY' with your actual API key
      const apiKey = 'YOUR_API_KEY';
      const apiUrl = `https://dictionaryapi.com/api/v3/references/collegiate/json/${word}?key=${apiKey}`;

      const response = await fetch(apiUrl);
      const data = await response.json();

        // Consider only the first object from the array
      const firstObject = data[0];

      setWordDetails(firstObject);
      } catch (error) {
      console.error('Error fetching word details:', error);
      }
};

if (word) {
      fetchWordDetails();
}
}, [word]);

return (
    <div>
      {wordDetails && (
        <div>
          <h2>{wordDetails.word}</h2>
          <p>
            <strong>Definition:</strong> {wordDetails.shortdef.join(', ')}
          </p>
          {wordDetails.fl && (
            <p>
              <strong>Part of Speech:</strong> {wordDetails.fl}
            </p>
          )}
          {wordDetails.syns && wordDetails.syns.length > 0 && (
            <p>
              <strong>Synonyms:</strong> {wordDetails.syns.join(', ')}
            </p>
          )}
          {/* Add more details as needed */}
        </div>
      )}
    </div>
  );
};

export default WordDetails;
